import React, { useState } from 'react';
import '../DropdownRegras/DropdownRegras.css';

function DropdownRegras() {
  const [aberto, setAberto] = useState(false);

  const toggleDropdown = () => {
    setAberto(!aberto);
  };

  return (
    <div className="dropdown-regras">
      <div
        className={`dropdown-titulo ${aberto ? 'aberto' : ''}`}
        onClick={toggleDropdown}
      >
        <span>Regras</span>
        <span className="icone-seta">{aberto ? '▲' : '▼'}</span>
      </div>
      {aberto && (
        <ol className="conteudo-regras">
          <li>Regra 1</li>
          <li>Regra 2</li>
          <li>Regra 3</li>
        </ol>
      )}
    </div>
  );
}

export default DropdownRegras;
