import React, { useState } from 'react';
import axios from 'axios';
import '../Formulario/Formulario.css';

function Formulario() {
  const [linhasProdutos, setLinhasProdutos] = useState([
    { produto: '', volume: '', preco: '', moeda: '' },
  ]);

  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    empresa: '',
    telefone: '',
  });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLinhaChange = (index, e) => {
    const { name, value } = e.target;
    const linhas = [...linhasProdutos];
    linhas[index][name] = value;
    setLinhasProdutos(linhas);
  };

  const adicionarLinha = () => {
    setLinhasProdutos([
      ...linhasProdutos,
      { produto: '', volume: '', preco: '', moeda: '' },
    ]);
  };

  const removerLinha = (index) => {
    const linhas = [...linhasProdutos];
    linhas.splice(index, 1);
    setLinhasProdutos(linhas);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = {
      data: {
        ...formData,
        produtos: JSON.stringify(linhasProdutos),
      },
    };

    try {
      const response = await axios.post(
        'https://sheetdb.io/api/v1/xglqqqggp4fn5',
        data,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': '45dmopewadeyj3qak5go8wxphq9yvuxjtmyzrw3p', // Descomente esta linha se necessário
          },
        }
      );
      alert('Formulário enviado com sucesso!');
      // Limpar o formulário se necessário
    } catch (error) {
      console.error('Erro ao enviar o formulário:', error);
      alert('Ocorreu um erro ao enviar o formulário. Por favor, tente novamente.');
    }
  };

  return (
    <div className="formulario-container">
      <h2 className="titulo-formulario">Formulário de Produtos</h2>
      <form onSubmit={handleSubmit} className="formulario">
        <input
          type="text"
          name="nome"
          placeholder="Nome"
          className="input-formulario"
          value={formData.nome}
          onChange={handleInputChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          className="input-formulario"
          value={formData.email}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="empresa"
          placeholder="Empresa"
          className="input-formulario"
          value={formData.empresa}
          onChange={handleInputChange}
          required
        />
        <input
          type="tel"
          name="telefone"
          placeholder="Telefone"
          className="input-formulario"
          value={formData.telefone}
          onChange={handleInputChange}
        />

        <h3 className="titulo-selecao-produtos">Seleção de produtos</h3>

        <div className="titulos-produtos">
          <span>Produto</span>
          <span>Volume</span>
          <span>Preço</span>
          <span>Moeda</span>
        </div>

        {linhasProdutos.map((linha, index) => (
          <div key={index} className="linha-produto">
            <select
              name="produto"
              className="input-produto"
              value={linha.produto}
              onChange={(e) => handleLinhaChange(index, e)}
              required
            >
              <option value="">Produto ▼</option>
              <option value="Produto 1">Produto 1</option>
              <option value="Produto 2">Produto 2</option>
              <option value="Produto 3">Produto 3</option>
            </select>
            <input
              type="number"
              name="volume"
              placeholder="Volume"
              className="input-volume"
              value={linha.volume}
              onChange={(e) => handleLinhaChange(index, e)}
              required
            />
            <input
              type="number"
              name="preco"
              placeholder="Preço"
              className="input-preco"
              value={linha.preco}
              onChange={(e) => handleLinhaChange(index, e)}
              required
            />
            <select
              name="moeda"
              className="input-moeda"
              value={linha.moeda}
              onChange={(e) => handleLinhaChange(index, e)}
              required
            >
              <option value="">Moeda ▼</option>
              <option value="USD">USD</option>
              <option value="BRL">BRL</option>
            </select>
            <button
              type="button"
              className="botao-excluir"
              onClick={() => removerLinha(index)}
            >
              X
            </button>
          </div>
        ))}

        <div className="adicionar-linha">
          <button type="button" className="botao-adicionar" onClick={adicionarLinha}>
            +
          </button>
          <span className="texto-adicionar">Clique para adicionar linha de produtos</span>
        </div>

        <button type="submit" className="botao-enviar">
          Enviar
        </button>
      </form>
    </div>
  );
}

export default Formulario;
